function renderPopup(o){let n='\n      <div id="popup_box">\n    <input type="button" value="X" id="cancel_button">\n    <p id="info_text">\n    <iframe style="height: 440px;border:none;width:100%;" id="load_ext" src ="chrome-extension://'+o+'/index.html"> </iframe>\n    </p>\n    </div>\n  ';$("body").append(n),$("#cancel_button").click((function(){hidepopup()})),$("#close_button").click((function(){hidepopup()})),showpopup()}function showpopup(){$("#popup_box").fadeToggle(),$("#popup_box").css({visibility:"visible",display:"block"})}function hidepopup(){$("#popup_box").fadeToggle(),$("#popup_box").css({visibility:"hidden",display:"none"})}chrome.runtime.onMessage.addListener((function(o,n,p){if(console.log("REQUEST FROM"+o.from),"background"===o.from){let n=o.chrom_id;renderPopup(n)}}));
